import React, {Component} from 'react';
import './Services.css'

class Services extends Component {
render() {
    return (
        <div className="services">
            <h3>Services</h3>
            <h2>What We Offer</h2>
            <div className="row">
                <Icons />
            </div>
        </div>
    );
}
}

const iconObj = [
    {
        icon: <i class="fas fa-mobile-alt"></i>,
        title: 'Responsive',
        description: 'Look great on my screen size!'
    },
    {
        icon: <i class="fas fa-pencil-alt"></i>,
        title: 'Redesigned',
        description: 'Freshly redisigned for bootstrap 4'
    },
    {
        icon: <i class="fas fa-thumbs-up"></i>,
        title: 'Favourited',
        description: 'Millions of users <i class="fas fa-heart"></i> Start using Bootstrap'
    },
    {
        icon: <i class="fas fa-brain"></i>,
        title: 'Question',
        description: 'Pick my brain'
    }
]

class Icons extends Component {
    render() {
        const iconLoader = iconObj.map( iconObj => {
            return (
                <div>
                    <span>{iconObj.icon}</span>
                    <h4>{iconObj.title}</h4>
                    <p>{iconObj.description}</p>
                </div>
            )
        });
        return (
            {iconLoader}
        )
    }
}

export default Services;
